package com.express.domain;import com.express.domain.Persistable;@SuppressWarnings("PMD")public class PersistableEqualityStrategy {   private Persistable entity;   public PersistableEqualityStrategy(Persistable entity) {      this.entity = entity;   }   public boolean entityEquals(Object second) {      if (entity == second) {         return true;      }      if (eitherIsNull(entity, second) || classesNotSame(entity, second)) {         return false;      }      return entity.getId().equals(((Persistable) second).getId());   }   public int entityHashCode(int fallback) {      return entity.getId() == null ? fallback : entity.getId().hashCode();   }   private boolean classesNotSame(Persistable first, Object second) {      return !first.getClass().equals(second.getClass());   }   private boolean eitherIsNull(Persistable first, Object second) {      return first == null || second == null;   }}