package com.express.domain;import org.junit.Before;import org.junit.Test;import org.unitils.UnitilsJUnit4;import java.util.Calendar;import static com.express.matcher.BeanMatchers.usesPersistableEqualityStrategy;import static com.express.matcher.BeanMatchers.usesPersistableHashCodeStrategy;import static com.express.matcher.BeanMatchers.usesReflectionToStringBuilder;import static org.hamcrest.MatcherAssert.assertThat;import static org.junit.Assert.assertEquals;/** * @author Adam Boas *         Created on Mar 23, 2009 */public class DailyIterationStatusRecordTest extends UnitilsJUnit4 {   private static final Calendar CAL = Calendar.getInstance();   private static final Iteration ITERATION = new Iteration();   private static final Integer EFFORT = 1;   private static final Integer TOTAL_POINTS = 2;   private static final Integer COMPLETED_POINTS = 3;   DailyIterationStatusRecord record;   @Before   public void setUp() {      record = new DailyIterationStatusRecord(CAL, EFFORT, TOTAL_POINTS, COMPLETED_POINTS, ITERATION);   }   @Test   public void testConstructorSetsFields() {      assertEquals(CAL, record.getDate());      assertEquals(EFFORT, record.getTaskHoursRemaining());      assertEquals(ITERATION, record.getIteration());      assertEquals(TOTAL_POINTS, record.getTotalPoints());      assertEquals(COMPLETED_POINTS, record.getCompletedPoints());   }   @Test   public void shouldBaseEqualityOnThePersistableEqualityStrategy() {      assertThat(record, usesPersistableEqualityStrategy());   }   @Test   public void shouldBaseHashCodeOnThePersistableEqualityStrategy() {      assertThat(record, usesPersistableHashCodeStrategy());   }   @Test   public void shouldUseReflectionToStringBuilder() {      assertThat(record, usesReflectionToStringBuilder());   }}