package com.express.domain;import org.junit.Before;import org.junit.Test;import static com.express.matcher.BeanMatchers.hasValidSettersAndGettersExcluding;import static com.express.matcher.BeanMatchers.usesPersistableEqualityStrategy;import static com.express.matcher.BeanMatchers.usesPersistableHashCodeStrategy;import static com.express.matcher.BeanMatchers.usesReflectionToStringBuilder;import static org.hamcrest.MatcherAssert.assertThat;/** * @author Adam Boas *         Created on Mar 28, 2009 */public class ProjectWorkerTest {      private ProjectWorker worker;      @Before   public void setUp() {      worker = new ProjectWorker();   }   @Test   public void shouldSetAndGetProperties() {      assertThat(worker, hasValidSettersAndGettersExcluding("equalityStrategy", "version"));   }   @Test   public void shouldBaseEqualityOnThePersistableEqualityStrategy() {      assertThat(worker, usesPersistableEqualityStrategy());   }   @Test   public void shouldBaseHashCodeOnThePersistableEqualityStrategy() {      assertThat(worker, usesPersistableHashCodeStrategy());   }   @Test   public void shouldUseReflectionToStringBuilder() {      assertThat(worker, usesReflectionToStringBuilder());   }}