package com.express.domain;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.junit.Before;import org.junit.Test;import org.unitils.UnitilsJUnit4;import static com.express.matcher.BeanMatchers.hasValidSettersAndGettersExcluding;import static com.express.matcher.BeanMatchers.usesPersistableEqualityStrategy;import static com.express.matcher.BeanMatchers.usesPersistableHashCodeStrategy;import static com.express.matcher.BeanMatchers.usesReflectionToStringBuilder;import static org.hamcrest.MatcherAssert.assertThat;public class ThemeTest extends UnitilsJUnit4 {   private static final Log LOG = LogFactory.getLog(ThemeTest.class);      private Theme theme;      @Before   public void setUp() {      theme = new Theme();   }   @Test   public void shouldSetAndGetProperties() {      assertThat(theme, hasValidSettersAndGettersExcluding("equalityStrategy", "version"));   }   @Test   public void shouldBaseEqualityOnThePersistableEqualityStrategy() {      assertThat(theme, usesPersistableEqualityStrategy());   }   @Test   public void shouldBaseHashCodeOnThePersistableEqualityStrategy() {      assertThat(theme, usesPersistableHashCodeStrategy());   }   @Test   public void shouldUseReflectionToStringBuilder() {      assertThat(theme, usesReflectionToStringBuilder());   }}