package com.express.matcher;import org.hamcrest.Factory;public final class BeanMatchers {   private BeanMatchers() { }   @Factory   public static <T> HasValidSettersAndGettersForMatcher hasValidSettersAndGettersFor(String... fields) {      return new HasValidSettersAndGettersForMatcher(fields);   }   @Factory   public static <T> HasValidSettersAndGettersExcludingMatcher hasValidSettersAndGettersExcluding(String... fields) {      return new HasValidSettersAndGettersExcludingMatcher(fields);   }   @Factory   public static <T> UsesPersistableEqualityStrategyMatcher usesPersistableEqualityStrategy() {      return new UsesPersistableEqualityStrategyMatcher();   }   @Factory   public static <T> UsesPersistableHashCodeStrategyMatcher usesPersistableHashCodeStrategy() {      return new UsesPersistableHashCodeStrategyMatcher();   }   @Factory   public static <T> UsesReflectionToStringBuilderMatcher usesReflectionToStringBuilder() {      return new UsesReflectionToStringBuilderMatcher();   }}