package com.express.matcher;import com.express.testutils.SetterGetterInvoker;import org.hamcrest.Description;import org.hamcrest.TypeSafeMatcher;@SuppressWarnings("PMD.ArrayIsStoredDirectly")public class HasValidSettersAndGettersExcludingMatcher<T> extends TypeSafeMatcher<T> {   private String[] fields;   public HasValidSettersAndGettersExcludingMatcher(String... fields) {      this.fields = fields;   }   @Override   public boolean matchesSafely(T subject) {      SetterGetterInvoker<T> setterGetterInvoker = new SetterGetterInvoker<T>(subject);      return setterGetterInvoker.invokeSettersAndGettersExcluding(fields);   }   public void describeTo(Description description) {      description.appendText("Unable to match setter and getter results for class fields excluding ");      description.appendValue(fields);   }}