package com.express.matcher;import com.express.testutils.SetterGetterInvoker;import org.hamcrest.Description;import org.hamcrest.TypeSafeMatcher;@SuppressWarnings("PMD.ArrayIsStoredDirectly")public class HasValidSettersAndGettersForMatcher<T> extends TypeSafeMatcher<T> {   private String[] fields;   public HasValidSettersAndGettersForMatcher(String... fields) {      this.fields = fields;   }   @Override   public boolean matchesSafely(T subject) {      SetterGetterInvoker<T> setterGetterInvoker = new SetterGetterInvoker<T>(subject);      return setterGetterInvoker.invokeSettersAndGettersFor(fields);   }   public void describeTo(Description description) {      description.appendText("Unable to match setter and getter results for class fields: ");      description.appendValue(fields);   }}