package com.express.matcher;import org.apache.commons.lang.builder.ReflectionToStringBuilder;import org.hamcrest.Description;import org.hamcrest.TypeSafeMatcher;public class UsesReflectionToStringBuilderMatcher<T> extends TypeSafeMatcher<T> {   @Override   public boolean matchesSafely(T subject) {      return subject.toString().equals(new ReflectionToStringBuilder(subject).toString());   }   public void describeTo(Description description) {      description.appendText("class is not using REflectionToStringBuilder to generate string representation");   }}